import logo from "./logo.svg";
import Tesseract from "tesseract.js";

import React, { useState } from "react";
import "./App.css";

function App() {
  const [imagePath, setImagePath] = useState(null);
  const [extractedText, setExtractedText] = useState(null);
  const tesseract = Tesseract;

  const handleImageUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      // You can use FileReader to read the contents of the file and obtain a data URL
      const reader = new FileReader();

      reader.onloadend = () => {
        const uploadedImagePath = reader.result;
        setImagePath(uploadedImagePath);
      };

      reader.readAsDataURL(file);
    }
  };
  const extractTextFromImage = (imagePath) => {
    Tesseract.recognize(
      imagePath,
      "eng", // Language: English
      {
        logger: (info) => console.log(info),
      }
    )
      .then(({ data: { text } }) => {
        console.log(text);
        setExtractedText(text);
      })
      .catch((error) => {
        console.error("Error extracting text:", error);
      });
  };

  //console.log(imagePath);

  return (
    <div className="App">
      <h1>Hi Tarique</h1>
      <form>
        <input
          type="file"
          accept="image/*"
          placeholder="upload image"
          onChange={handleImageUpload}
        />
        {/* <button type="submit">Upload</button> */}
        <button onClick={extractTextFromImage} type="submit">
          Extract
        </button>
      </form>
      {/* <img src={logo} */}
      <section class="flex">
        {imagePath && (
          <div>
            <p>Uploaded Image:</p>
            <img
              src={imagePath}
              alt="Uploaded"
              style={{ maxWidth: "100%", maxHeight: "300px" }}
            />
          </div>
        )}
        <div>

            <div>
              <p>Extracted Text:</p>
              <pre>{extractedText}</pre>
            </div>
          )}
        </div>
      </section>
    </div>
  );
}

export default App;
