import logo from "./logo.svg";
import Tesseract from "tesseract.js";

import React, { useState } from "react";
import "./App.css";

function App() {
  const [imagePath, setImagePath] = useState(null);
  const tesseract = Tesseract;

  const handleImageUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      // You can use FileReader to read the contents of the file and obtain a data URL
      const reader = new FileReader();

      reader.onloadend = () => {
        const uploadedImagePath = reader.result;
        setImagePath(uploadedImagePath);
      };

      reader.readAsDataURL(file);
    }
  };

  console.log(imagePath);

  const handleExtract = () => {
    tesseract.recognize(imagePath, "eng").then(({ data: { text } }) => {
      console.log(text);
    });
  };

  return (
    <div className="App">
      <h1>Hi Tarique</h1>
      <form>
        <input
          type="file"
          accept="image/*"
          placeholder="upload image"
          onChange={handleImageUpload}
        />
        {/* <button type="submit">Upload</button> */}
        <button onClick={handleExtract} type="submit">
          Extract
        </button>
      </form>
      {/* <img src={logo} */}
      <section class="flex">
        {imagePath && (
          <div>
            <p>Uploaded Image:</p>
            <img
              src={imagePath}
              alt="Uploaded"
              style={{ maxWidth: "100%", maxHeight: "300px" }}
            />
          </div>
        )}
        <div></div>
      </section>
    </div>
  );
}

export default App;

